import React, { useState, useEffect } from 'react';
import { Collapse, CardBody, Card, CardTitle, Input, } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronUp } from '@fortawesome/free-solid-svg-icons';
import { Form, Button, Row, Container, Col, FormGroup } from 'react-bootstrap';
import { useGlobalContext } from '../../GlobalContext';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide } from 'react-toastify';


function SLMOptions() {


    const { serverInfo, slmOptions, setSLMOptions } = useGlobalContext();

    const toggle = () => {
        setSLMOptions(prev => ({ ...prev, isCollapsed: !prev.isCollapsed }));
    };

    const handleUpdateAffine = () => {
    }




    // const [hologramOptionsWebsocket, sethologramOptionsWebsocket] = useState(null);

    // const hologramOptionsServerURL = `http://${serverInfo.hologramOptionsserver[1].ip}:${serverInfo.hologramOptionsserver[1].portHTTP}`;


    // const [imageKey, setImageKey] = useState(Date.now());

    // useEffect(() => {
    //     const interval = setInterval(() => {
    //         setImageKey(Date.now());
    //     }, 2000);
    //     return () => clearInterval(interval);
    // }, []);


    // const handleUpdateClick = () => {

    //     // check the if the set point is a valid number
    //     if (isNaN(hologramOptions.setPointSetAt)) {
    //         showToast('Set Point should be a valid number', 'error');
    //         return;
    //     }

    //     // check for min max values
    //     if (hologramOptions.setPointSetAt < hologramOptions.minSetPoint || hologramOptions.setPointSetAt > hologramOptions.maxSetPoint) {
    //         showToast(`Set Point should be between ${hologramOptions.minSetPoint} and ${hologramOptions.maxSetPoint}`, 'error');
    //         return;
    //     }

    //     // post temperature in json to 10.0.63.153:4031/set_temperature
    //     axios.post(`${hologramOptionsServerURL}/set_temperature`, {
    //         temperature: hologramOptions.setPointSetAt
    //     })
    //         .then((response) => {
    //             showToast('Set Point Updated Successfully', 'success');
    //         })
    //         .catch((error) => {
    //             showToast('Failed to update Set Point', 'error');
    //         });

    // }

    // const showToast = (msg, type, delay = 2000) => {
    //     toast[type](msg, {
    //         position: "bottom-right",
    //         autoClose: delay,
    //         hideProgressBar: false,
    //         closeOnClick: true,
    //         pauseOnHover: true,
    //         draggable: true,
    //         progress: undefined,
    //         theme: "dark",
    //         transition: Slide,
    //     });
    // };



    // useEffect(() => {


    //     const connectToWebSocketForTrackDetails = () => {
    //         if (hologramOptionsWebsocket) {
    //             return;
    //         }

    //         const ws = new WebSocket(`ws://${serverInfo.hologramOptionsserver[1].ip}:${serverInfo.hologramOptionsserver[1].portWS}/ws`);
    //         console.log("Trying to connect to websocket: Objective hologramOptions")
    //         ws.onmessage = (event) => {
    //             const data = JSON.parse(event.data);
    //             sethologramOptions(prev => ({
    //                 ...prev,
    //                 setPoint: data.set_point,
    //                 objectiveTemperature: data.objective_temperature,
    //                 lastUpdated: new Date().toLocaleTimeString(),
    //                 // timeSeriesSetPoints : data.time_series_set_points,
    //                 // timeSeriesObjectiveTemperatures : data.time_series_objective_temperatures,
    //                 // timeSeriesTimes : data.time_series_times
    //             }));
    //         };

    //         ws.onopen = () => {
    //             console.log(`Connected to objective hologramOptions server`);
    //         };
    //         ws.onerror = (error) => console.error('WebSocket error track server:', error);
    //         ws.onclose = () => {
    //             sethologramOptions(prev => ({ ...prev, isOnline: false }));
    //             sethologramOptionsWebsocket(null);
    //             console.log(`Disconnected from objective hologramOptions server`);
    //             setTimeout(() => connectToWebSocketForTrackDetails(), 1000);
    //         };

    //         sethologramOptionsWebsocket(ws);
    //     };

    //     connectToWebSocketForTrackDetails();


    // }, []);


    // useEffect(() => {

    //     const intervalId = setInterval(() =>
    //         axios.get(`${hologramOptionsServerURL}/heartbeat`)
    //             .then(() => {
    //                 sethologramOptions(prev => ({ ...prev, isOnline: true }));
    //             })
    //             .catch(() => {
    //                 sethologramOptions(prev => ({ ...prev, isOnline: false }));
    //             })
    //         , 1000);

    //     return () => clearInterval(intervalId);

    // }, []);





    return (
        <div>
            <div onClick={toggle} style={{ backgroundColor: "white", display: 'flex', justifyContent: 'space-between', borderTopLeftRadius: '10px', borderTopRightRadius: '10px', borderBottomLeftRadius: '0px', borderBottomRightRadius: '0px' }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <div className='fs-5 fw-bold' style={{ margin: '0px', padding: '10px', color: "black" }}>
                        SLM Options
                    </div>

                    <div style={{
                        height: '10px',
                        width: '10px',
                        backgroundColor: slmOptions.isOnline ? 'green' : 'red',
                        borderRadius: '50%',
                        display: 'inline-block',
                        marginLeft: '5px'
                    }}>
                    </div>
                </div>

                <div className='fs-5 fw-bold' style={{ marginLeft: 'auto', padding: '10px', color: "black" }}>
                    <FontAwesomeIcon icon={slmOptions.isCollapsed ? faChevronUp : faChevronDown} />
                </div>
            </div>

            <Collapse isOpen={slmOptions.isCollapsed}>
                <Card className="rounded-bottom">
                    <CardBody>
                        <h5>Add point:</h5>
                        <div className='container'>
                            <div className="row">

                                <div className="row form-group" key={num} style={{ marginBottom: '0' }}>
                                    <div className="col-md-3" style={{ padding: '0' }}>
                                        <input
                                            type="number"
                                            id={`x_point`}
                                            className="form-control"
                                            placeholder={`x`}
                                            style={{ margin: '0', borderRadius: '0' }}
                                        />
                                    </div>
                                    <div className="col-md-3" style={{ padding: '0' }}>
                                        <input
                                            type="number"
                                            id={`y_point`}
                                            className="form-control"
                                            placeholder={`y`}
                                            style={{ margin: '0', borderRadius: '0' }}
                                        />
                                    </div>
                                    <div className="col-md-3" style={{ padding: '0' }}>
                                        <input
                                            type="number"
                                            id={`z_point`}
                                            className="form-control"
                                            placeholder={`z`}
                                            style={{ margin: '0', borderRadius: '0' }}
                                        />
                                    </div>
                                    <div className="col-md-3" style={{ padding: '0' }}>
                                        <input
                                            type="number"
                                            id={`intensity_point`}
                                            className="form-control"
                                            placeholder={`i`}
                                            style={{ margin: '0', borderRadius: '0' }}
                                        />
                                    </div>
                                </div>
                            </div>

                        </div>
                        <h5>Affine Parameters:</h5>
                        <div className="container">

                            <div className="row">

                                <Form>
                                    {['0', '1', '2'].map((num) => (
                                        <div className="row form-group" key={num} style={{ marginBottom: '0' }}>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`SX${num}`}
                                                    className="form-control"
                                                    placeholder={`SX${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`SY${num}`}
                                                    className="form-control"
                                                    placeholder={`SY${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`CX${num}`}
                                                    className="form-control"
                                                    placeholder={`CX${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`CY${num}`}
                                                    className="form-control"
                                                    placeholder={`CY${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                        </div>
                                    ))}
                                    <button type="submit" className="btn btn-primary" style={{ width: '100%', marginTop: '10px' }}>Update Affine</button>
                                </Form>





                            </div>
                        </div>
                    </CardBody>
                </Card>
            </Collapse>

        </div>
    );
}

