import React, { useState, useEffect, useRef } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCog } from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide } from 'react-toastify';
import axios from 'axios';

const mainServerURL = "http://10.0.63.153:4000/";
const camServerURLs = {
  cam1: "http://10.0.63.153:4001",
  cam2: "http://10.0.63.153:4002"
};

const useCameraSettings = (cameraKey, initialValues, allCameras) => {
  const [formValues, setFormValues] = useState(initialValues);
  const [comment, setComment] = useState("Select the camera ID from the dropdown list");
  const [isVirtual, setIsVirtual] = useState(false);

  useEffect(() => {
    const selectedCamera = allCameras.find((camera) => camera.camera_id === formValues[`${cameraKey}Id`]);
    if (!selectedCamera) return;

    const virtual = !selectedCamera.details.is_real;
    setIsVirtual(virtual);
    setComment(`${selectedCamera.details.comment} | ${virtual ? "Virtual Camera" : "Real Camera"}`);
  }, [formValues, allCameras, cameraKey]);

  const handleInputChange = (event) => {
    setFormValues({
      ...formValues,
      [event.target.name]: event.target.value,
    });
  };

  return { formValues, handleInputChange, comment, isVirtual };
};

const CameraModal = ({ show, handleClose, cameraKey, header, formValues, handleInputChange, comment, isVirtual, allCameras }) => (
  <Modal show={show} onHide={handleClose}>
    <Modal.Header closeButton>
      <Modal.Title>{header}</Modal.Title>
    </Modal.Header>
    <Modal.Body>
      <Form>
        <Form.Group className="mb-3">
          <Form.Label>Camera ID</Form.Label>
          <Form.Select name={`${cameraKey}Id`} onChange={handleInputChange} value={formValues[`${cameraKey}Id`]}>
            <option value="">Select Option</option>
            {allCameras.map((camera, index) => (
              <option key={index} value={camera.camera_id}>
                {camera.camera_id}
              </option>
            ))}
          </Form.Select>
          <Form.Text className="text-muted">{comment}</Form.Text>
        </Form.Group>
        {isVirtual && (
          <Form.Group className="mb-3">
            <Form.Label>Folder Path</Form.Label>
            <Form.Control name={`${cameraKey}FolderPath`} placeholder="Enter folder path" onChange={handleInputChange} value={formValues[`${cameraKey}FolderPath`]} />
          </Form.Group>
        )}
        <Form.Group className="mb-3">
          <Form.Label>Target Streaming FPS</Form.Label>
          <Form.Control name={`${cameraKey}TargetFPS`} type="number" placeholder="Enter FPS" onChange={handleInputChange} value={formValues[`${cameraKey}TargetFPS`]} />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Exposure Time (ms)</Form.Label>
          <Form.Control name={`${cameraKey}ExposureTime`} type="number" step="0.01" placeholder="Enter exposure time in ms" onChange={handleInputChange} value={formValues[`${cameraKey}ExposureTime`]} />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Gain</Form.Label>
          <Form.Control name={`${cameraKey}Gain`} type="number" step="0.01" placeholder="Enter gain" onChange={handleInputChange} value={formValues[`${cameraKey}Gain`]} />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Image Format</Form.Label>
          <Form.Select name={`${cameraKey}ImageFormat`} onChange={handleInputChange} value={formValues[`${cameraKey}ImageFormat`]}>
            <option value="compressed">Compressed</option>
            <option value="original">Original</option>
          </Form.Select>
        </Form.Group>
      </Form>
    </Modal.Body>
  </Modal>
);

const CameraPreview = () => {
  const [allCameras, setAllCameras] = useState([]);
  const [showModalSettings, setShowModalSettings] = useState({ cam1: false, cam2: false });
  const [isSwitchActive, setIsSwitchActive] = useState({ cam1: true, cam2: true });
  const [isStreaming, setIsStreaming] = useState({ cam1: false, cam2: false });
  const [isPreview, setIsPreview] = useState({ cam1: false, cam2: false });
  const [fps, setFps] = useState({ cam1: 0, cam2: 0 });
  const [rxSpeed, setRxSpeed] = useState({ cam1: 0, cam2: 0 });
  const canvasRef = useRef(null);

  const cam1InitialValues = {
    cam1Id: 'Select Option',
    cam1FolderPath: 'F:/',
    cam1TargetFPS: '10',
    cam1ImageFormat: '',
    cam1ExposureTime: "10.0",
    cam1Gain: "1.0",
  };

  const cam2InitialValues = {
    cam2Id: 'Select Option',
    cam2FolderPath: 'F:/',
    cam2TargetFPS: '10',
    cam2ImageFormat: '',
    cam2ExposureTime: "10.0",
    cam2Gain: "1.0",
  };

  const { formValues: cam1FormValues, handleInputChange: handleCam1InputChange, comment: cam1Comment, isVirtual: cam1IsVirtual } = useCameraSettings('cam1', cam1InitialValues, allCameras);
  const { formValues: cam2FormValues, handleInputChange: handleCam2InputChange, comment: cam2Comment, isVirtual: cam2IsVirtual } = useCameraSettings('cam2', cam2InitialValues, allCameras);

  useEffect(() => {
    if (showModalSettings.cam1 || showModalSettings.cam2) {
      axios.get(camServerURLs.cam1 + "/get_all_camera_details")
        .then((response) => {
          setAllCameras(response.data.data);
        })
        .catch((error) => {
          showToast("Error in fetching camera details", "error", 2000);
          showToast(error.message, "error", 2000);
        });
    }
  }, [showModalSettings]);

  const showToast = (msg, type, delay) => {
    toast[type](msg, {
      position: "bottom-right",
      autoClose: delay,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      theme: "dark",
      transition: Slide,
    });
  };

  const togglePreview = (camKey) => {
    const isPreviewing = isPreview[camKey];
    const url = mainServerURL + (isPreviewing ? "shut/camserver/" : "warmup/camserver/") + (camKey === "cam1" ? "1" : "2");
    const toastMsg = isPreviewing ? "Shutting down" : "Warming up";

    setIsSwitchActive((prev) => ({ ...prev, [camKey]: false }));

    let toastId = toast.loading(`${toastMsg} ${camKey === "cam1" ? "Cam 1" : "Cam 2"} Server`, {
      position: "top-center",
      autoClose: 2000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      theme: "dark",
      transition: Slide,
    });

    axios.get(url)
      .then((response) => {
        const success = response.data.success;
        const msg = response.data.msg;
        setIsSwitchActive((prev) => ({ ...prev, [camKey]: true }));

        if (success) {
          toast.update(toastId, {
            render: msg,
            type: "success",
            isLoading: false,
            autoClose: 2000,
          });
          setIsPreview((prev) => ({ ...prev, [camKey]: !isPreviewing }));
          setIsStreaming((prev) => ({ ...prev, [camKey]: false }));
        } else {
          toast.update(toastId, {
            render: msg,
            type: "error",
            isLoading: false,
            autoClose: 2000,
          });
        }
      })
      .catch((error) => {
        showToast(`Error in ${toastMsg.toLowerCase()} Cam Server ${camKey === "cam1" ? "1" : "2"}`, "error", 2000);
        showToast(error.message, "error", 2000);
      });
  };

  const streamCamera = (camKey, data) => {
    const isStreamingCam = isStreaming[camKey];
    const url = camServerURLs[camKey] + (isStreamingCam ? "/stop_camera/" : "/start_camera/");
    const toastMsg = isStreamingCam ? "Stopping" : "Starting";

    setIsSwitchActive((prev) => ({ ...prev, [camKey]: false }));

    let toastId = toast.loading(`${toastMsg} ${camKey === "cam1" ? cam1FormValues.cam1Id : cam2FormValues.cam2Id}`, {
      position: "top-center",
      autoClose: 2000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      theme: "dark",
      transition: Slide,
    });

    axios.post(url, data)
      .then((response) => {
        const success = response.data.success;
        const msg = response.data.msg;
        setIsSwitchActive((prev) => ({ ...prev, [camKey]: true }));

        if (success) {
          toast.update(toastId, {
            render: msg,
            type: "success",
            isLoading: false,
            autoClose: 2000,
          });
          setIsStreaming((prev) => ({ ...prev, [camKey]: !isStreamingCam }));
        } else {
          toast.update(toastId, {
            render: msg,
            type: "error",
            isLoading: false,
            autoClose: 2000,
          });
        }
      })
      .catch((error) => {
        showToast(`Error in ${toastMsg.toLowerCase()} camera ${camKey === "cam1" ? "1" : "2"}`, "error", 2000);
        showToast(error.message, "error", 2000);
      });
  };

  useEffect(() => {
    if (canvasRef.current) {
      // setCanvasSize({
      //   width: canvasRef.current.offsetWidth,
      //   height: canvasRef.current.offsetHeight,
      // });
    }
  }, []);

  const handleWebSocket = (camKey, url, img) => {
    let frameCount = 0;
    let dataCount = 0;
    let firstImageReceived = false;
    const websocket = new WebSocket(url);
    websocket.binaryType = 'blob';

    const updateFpsAndSpeed = () => {
      setFps((prev) => ({ ...prev, [camKey]: frameCount }));
      setRxSpeed((prev) => ({ ...prev, [camKey]: dataCount / 1024 }));
      frameCount = 0;
      dataCount = 0;
    };

    const intervalId = setInterval(updateFpsAndSpeed, 1000);

    img.onload = () => {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      if (!firstImageReceived) {
        canvas.width = img.width;
        canvas.height = img.height;
        firstImageReceived = true;
      }
      ctx.drawImage(img, 0, 0, img.width, img.height, 0, 0, canvas.width, canvas.height);
      URL.revokeObjectURL(img.src);
    };

    websocket.onmessage = (event) => {
      const imageUrl = URL.createObjectURL(event.data);
      img.src = imageUrl;
      frameCount++;
      dataCount += event.data.size;
    };

    websocket.onopen = () => console.log('Connected to server');
    websocket.onerror = (error) => console.error('WebSocket error:', error);
    websocket.onclose = () => {
      console.log('Disconnected from server');
      clearInterval(intervalId);
    };

    return () => {
      websocket.close();
      clearInterval(intervalId);
    };
  };

  useEffect(() => {
    if (!isPreview.cam1) return;
    const ip = camServerURLs.cam1.replace(/(^\w+:|^)\/\//, '');
    const img = new Image();
    return handleWebSocket('cam1', `ws://${ip}/ws`, img);
  }, [isPreview.cam1]);

  useEffect(() => {
    if (!isPreview.cam2) return;
    const ip = camServerURLs.cam2.replace(/(^\w+:|^)\/\//, '');
    const img = new Image();
    return handleWebSocket('cam2', `ws://${ip}/ws`, img);
  }, [isPreview.cam2]);

  return (
    <div className="p-3">
      {["cam1", "cam2"].map((camKey) => (
        <div key={camKey} className="d-flex flex-row justify-content-between align-items-center mb-3">
          <div className="d-flex justify-content-center align-items-center">
            <span className="me-3 fs-4 fw-bold" style={{ color: !isSwitchActive[camKey] ? 'gray' : 'inherit' }}>
              {camKey === "cam1" ? "Cam 1" : "Cam 2"}
            </span>
            <div className="form-check form-switch fs-5">
              <input
                className="form-check-input"
                type="checkbox"
                role="switch"
                disabled={!isSwitchActive.cam1 || !isSwitchActive.cam2}
                checked={isPreview[camKey]}
                onChange={() => togglePreview(camKey)}
              />
            </div>
            <div className="d-flex justify-content-center" style={{ marginLeft: '30px' }}>
              <div className="btn-group">
                <Button variant="primary" onClick={() => setShowModalSettings((prev) => ({ ...prev, [camKey]: true }))} disabled={!isPreview[camKey] || !isSwitchActive[camKey] || isStreaming[camKey]}>
                  <FontAwesomeIcon icon={faCog} />
                </Button>
                <Button
                  variant="primary"
                  onClick={() => streamCamera(camKey, camKey === "cam1" ? cam1FormValues : cam2FormValues)}
                  disabled={!isPreview[camKey] || !isSwitchActive[camKey]}
                  style={{ backgroundColor: isStreaming[camKey] ? 'orange' : '' }}
                >
                  {isStreaming[camKey] ? 'Stop Stream' : 'Start Stream'}
                </Button>
              </div>
            </div>
            {isStreaming[camKey] && (
              <div className="d-flex justify-content-center" style={{ marginLeft: '30px' }}>
                <div>FPS: {fps[camKey].toFixed(2)} | Rx Speed: {(rxSpeed[camKey] / 1024).toFixed(2)} Mb/s</div>
              </div>
            )}
          </div>
        </div>
      ))}
      <div style={{ width: '100%', height: '100%', marginTop: '20px' }}>
        <canvas ref={canvasRef} style={{ width: '100%', height: '100%', backgroundColor: 'black' }}></canvas>
      </div>
      <CameraModal
        show={showModalSettings.cam1}
        handleClose={() => setShowModalSettings((prev) => ({ ...prev, cam1: false }))}
        cameraKey="cam1"
        header="Cam Server 1 Configuration"
        formValues={cam1FormValues}
        handleInputChange={handleCam1InputChange}
        comment={cam1Comment}
        isVirtual={cam1IsVirtual}
        allCameras={allCameras}
      />
      <CameraModal
        show={showModalSettings.cam2}
        handleClose={() => setShowModalSettings((prev) => ({ ...prev, cam2: false }))}
        cameraKey="cam2"
        header="Cam Server 2 Configuration"
        formValues={cam2FormValues}
        handleInputChange={handleCam2InputChange}
        comment={cam2Comment}
        isVirtual={cam2IsVirtual}
        allCameras={allCameras}
      />
      <ToastContainer />
    </div>
  );
};

export default CameraPreview;
