import React, { useState, useEffect, useRef } from 'react';
import { Collapse, CardBody, Card, Modal, Form as BootstrapForm, Button } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronUp } from '@fortawesome/free-solid-svg-icons';
import { useGlobalContext } from '../../GlobalContext';
// import buttongr
import { ButtonGroup } from 'react-bootstrap';

function SLMOptions() {
    const { serverInfo, slmOptions, setSLMOptions, canvasRefs } = useGlobalContext();
    const [activeButtonSpotAction, setActiveButtonSpotAction] = useState("Add");
    const [modal, setModal] = useState(false);
    const [currentPoint, setCurrentPoint] = useState({});
    const [modalPosition, setModalPosition] = useState({ x: 0, y: 0 });

    const toggle = () => {
        setSLMOptions(prev => ({ ...prev, isCollapsed: !prev.isCollapsed }));
    };

    const mousePosition = useRef({ x: 0, y: 0 });
    const [points, setPoints] = useState({});
    const selectedPoint = useRef(null);

    const findNearestPoint = (points, x, y) => {
        let nearestPointKey = null;
        let minDistance = Infinity;

        for (const key in points) {
            const point = points[key];
            const distance = Math.pow(point[0] - x, 2) + Math.pow(point[1] - y, 2);
            if (distance < minDistance) {
                minDistance = distance;
                nearestPointKey = key;
            }
        }

        return { nearestPointKey, minDistance };
    };

    useEffect(() => {
        const canvas = canvasRefs[4].current;

        const addPoint = () => {
            const { x, y } = mousePosition.current;
            const key = `${x}_${y}`;
            const newPoint = [x, y, slmOptions.currentZ, slmOptions.currentIntensity];
            setPoints(prev => ({ ...prev, [key]: newPoint }));
        };

        const removePoint = () => {
            const { x, y } = mousePosition.current;
            const { nearestPointKey, minDistance } = findNearestPoint(points, x, y);
            if (minDistance > 100) return;
            setPoints(prev => {
                const newPoints = { ...prev };
                delete newPoints[nearestPointKey];
                return newPoints;
            });
        };

        const handleMouseMove = (event) => {
            const rect = canvas.getBoundingClientRect();
            const mouseX = event.clientX - rect.left;
            const mouseY = event.clientY - rect.top;
            mousePosition.current = { x: mouseX, y: mouseY };
            if (selectedPoint.current) {
                const point = points[selectedPoint.current];
                point[0] = mouseX;
                point[1] = mouseY;
                setPoints(prev => ({ ...prev, [selectedPoint.current]: point }));
            }
        };

        const handleKeyPress = (event) => {
            switch (event.key.toUpperCase()) {
                case 'A':
                    addPoint();
                    break;
                case 'R':
                    removePoint();
                    break;
                default:
                    break;
            }
        };

        const handleMouseDown = (event) => {
            if (event.button === 2) {
                event.preventDefault();
                const { x, y } = mousePosition.current;
                const { nearestPointKey, minDistance } = findNearestPoint(points, x, y);
                if (minDistance <= 100) {
                    setCurrentPoint(points[nearestPointKey]);
                    setModalPosition({ x: event.clientX, y: event.clientY });
                    setModal(true);
                }
                return;
            }

            const { x, y } = mousePosition.current;
            const { nearestPointKey, minDistance } = findNearestPoint(points, x, y);
            if (minDistance > 100) {
                addPoint();
            } else {
                selectedPoint.current = nearestPointKey;
            }
        };

        const handleMouseUp = () => {
            selectedPoint.current = null;
        };

        window.addEventListener('mousemove', handleMouseMove);
        window.addEventListener('keypress', handleKeyPress);
        window.addEventListener('mousedown', handleMouseDown);
        window.addEventListener('mouseup', handleMouseUp);

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
            window.removeEventListener('keypress', handleKeyPress);
            window.removeEventListener('mousedown', handleMouseDown);
            window.removeEventListener('mouseup', handleMouseUp);
        };
    }, [points, canvasRefs, slmOptions]);

    const interpolateColor = (color1, color2, factor) => {
        const result = color1.slice();
        for (let i = 0; i < 3; i++) {
            result[i] = Math.round(result[i] + factor * (color2[i] - color1[i]));
        }
        return result;
    };

    const blue = [0, 0, 255];
    const purple = [128, 0, 128];

    useEffect(() => {
        const canvas = canvasRefs[4].current;
        const context = canvas.getContext('2d');
        context.clearRect(0, 0, canvas.width, canvas.height);
        for (const key in points) {
            const point = points[key];
            const color = interpolateColor(blue, purple, point[3]);
            context.beginPath();
            context.arc(point[0], point[1], 5, 0, 2 * Math.PI);
            context.fillStyle = `rgb(${color[0]}, ${color[1]}, ${color[2]})`;
            context.fill();
        }

        if (selectedPoint.current) {
            const point = points[selectedPoint.current];
            context.beginPath();
            context.arc(point[0], point[1], 10, 0, 2 * Math.PI);
            context.strokeStyle = 'white';
            context.stroke();
        }
    }, [points, canvasRefs[4]]);

    const handleModalSave = () => {
        const updatedPoints = { ...points };
        updatedPoints[selectedPoint.current] = currentPoint;
        setPoints(updatedPoints);
        setModal(false);
    };

    return (
        <div>
            <div onClick={toggle} style={{ backgroundColor: "white", display: 'flex', justifyContent: 'space-between', borderTopLeftRadius: '10px', borderTopRightRadius: '10px', borderBottomLeftRadius: '0px', borderBottomRightRadius: '0px' }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <div className='fs-5 fw-bold' style={{ margin: '0px', padding: '10px', color: "black" }}>
                        SLM Options
                    </div>
                    <div style={{
                        height: '10px',
                        width: '10px',
                        backgroundColor: slmOptions.isOnline ? 'green' : 'red',
                        borderRadius: '50%',
                        display: 'inline-block',
                        marginLeft: '5px'
                    }}></div>
                </div>
                <div className='fs-5 fw-bold' style={{ marginLeft: 'auto', padding: '10px', color: "black" }}>
                    <FontAwesomeIcon icon={slmOptions.isCollapsed ? faChevronUp : faChevronDown} />
                </div>
            </div>
            <Collapse isOpen={slmOptions.isCollapsed}>
                <Card className="rounded-bottom">
                    <CardBody>
                        <h5>Focal Spots:</h5>
                        <div className='container'>
                            <div className="row">
                                <BootstrapForm className="container">
                                    <div className="row form-group" style={{ marginBottom: '0' }}>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input type="number" id={`x_point`} className="form-control" placeholder={`x`} style={{ margin: '0', borderRadius: '0' }} />
                                        </div>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input type="number" id={`y_point`} className="form-control" placeholder={`y`} style={{ margin: '0', borderRadius: '0' }} />
                                        </div>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input type="number" id={`z_point`} className="form-control" placeholder={`z`} style={{ margin: '0', borderRadius: '0' }} />
                                        </div>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input type="number" id={`intensity_point`} className="form-control" placeholder={`i`} style={{ margin: '0', borderRadius: '0' }} />
                                        </div>
                                    </div>
                                </BootstrapForm>
                                <div className="row mt-3" style={{ marginBottom: '0' }}>
                                    <ButtonGroup aria-label="Button group">
                                        {['Add', 'Remove', 'Drag'].map((action) => (
                                            <Button key={action} onClick={() => setActiveButtonSpotAction(action)} variant={activeButtonSpotAction === action ? 'primary' : 'secondary'}>
                                                {action}
                                            </Button>
                                        ))}
                                    </ButtonGroup>
                                </div>
                                <Button type="submit" className="btn btn-primary" style={{ width: '100%', marginTop: '10px' }}>Process</Button>
                            </div>
                        </div>
                        <h5>Affine Parameters:</h5>
                        <div className="container">
                            <div className="row">
                                <BootstrapForm>
                                    {['0', '1', '2'].map((num) => (
                                        <div className="row form-group" key={num} style={{ marginBottom: '0' }}>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input type="number" id={`SX${num}`} className="form-control" placeholder={`SX${num}`} style={{ margin: '0', borderRadius: '0' }} />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input type="number" id={`SY${num}`} className="form-control" placeholder={`SY${num}`} style={{ margin: '0', borderRadius: '0' }} />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input type="number" id={`CX${num}`} className="form-control" placeholder={`CX${num}`} style={{ margin: '0', borderRadius: '0' }} />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input type="number" id={`CY${num}`} className="form-control" placeholder={`CY${num}`} style={{ margin: '0', borderRadius: '0' }} />
                                            </div>
                                        </div>
                                    ))}
                                </BootstrapForm>
                                <Button type="submit" className="btn btn-primary" style={{ width: '100%', marginTop: '10px' }}>Update Affine</Button>
                            </div>
                        </div>
                    </CardBody>
                </Card>
            </Collapse>
            <Modal isOpen={modal} toggle={() => setModal(!modal)} style={{ top: modalPosition.y, left: modalPosition.x, transform: 'translate(-50%, -50%)' }}>
                <Modal.Header toggle={() => setModal(!modal)}>Edit Point</Modal.Header>
                <Modal.Body>
                    <BootstrapForm>
                        <BootstrapForm.Group>
                            <BootstrapForm.Label>X</BootstrapForm.Label>
                            <BootstrapForm.Control type="number" value={currentPoint[0]} onChange={(e) => setCurrentPoint([+e.target.value, currentPoint[1], currentPoint[2], currentPoint[3]])} />
                        </BootstrapForm.Group>
                        <BootstrapForm.Group>
                            <BootstrapForm.Label>Y</BootstrapForm.Label>
                            <BootstrapForm.Control type="number" value={currentPoint[1]} onChange={(e) => setCurrentPoint([currentPoint[0], +e.target.value, currentPoint[2], currentPoint[3]])} />
                        </BootstrapForm.Group>
                        <BootstrapForm.Group>
                            <BootstrapForm.Label>Z</BootstrapForm.Label>
                            <BootstrapForm.Control type="number" value={currentPoint[2]} onChange={(e) => setCurrentPoint([currentPoint[0], currentPoint[1], +e.target.value, currentPoint[3]])} />
                        </BootstrapForm.Group>
                        <BootstrapForm.Group>
                            <BootstrapForm.Label>Intensity</BootstrapForm.Label>
                            <BootstrapForm.Control type="number" value={currentPoint[3]} onChange={(e) => setCurrentPoint([currentPoint[0], currentPoint[1], currentPoint[2], +e.target.value])} />
                        </BootstrapForm.Group>
                    </BootstrapForm>
                </Modal.Body>
                <Modal.Footer>
                    <Button color="primary" onClick={handleModalSave}>Save</Button>
                    <Button color="secondary" onClick={() => setModal(false)}>Cancel</Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

export default SLMOptions;
