import React, { createContext, useRef, useContext } from 'react';

const CanvasContext = createContext({ canvas1: null, canvas2: null });

export const useCanvases = () => {
  return useContext(CanvasContext);
};

export const CanvasProvider = ({ children }) => {

    const canvasRefs = {
        1: useRef(null),
        2: useRef(null),
      };

    const imageProps = {
      "width": useRef(-1),
      "height": useRef(-1),
    }

    const drawProps = {
      "scale": useRef(1),
      "startX": useRef(1),
      "startY": useRef(1),
      "endX": useRef(1),
      "endY": useRef(1),
      "centerX": useRef(1),
      "centerY": useRef(1)
    }

 
 
  return (
    <CanvasContext.Provider value={{ canvasRefs, imageProps, drawProps }} >
      {children}
    </CanvasContext.Provider>
  );
};
