import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Ensure Bootstrap CSS is imported


import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide } from 'react-toastify';


function ImageViewer() {
    const [imageSrc, setImageSrc] = useState('');
    const [fps, setFps] = useState(0);
    const [speed, setSpeed] = useState(0);
    const [dataReceived, setDataReceived] = useState(0);

    const [isChecked, setIsChecked] = useState(true); // Initialize to true if you want it checked initially

    // Handler to toggle the checked state
    const handleCheckboxChange = () => {
        setIsChecked(!isChecked);
    };

    const [someVariable, setSomeVariable] = useState('');

    // useEffect(() => {

    //     let x = 0;

    //     const intervalId = setInterval(() => {
    //         x++;
    //         const copyX = x;
    //         setSomeVariable(copyX);
    //     }
    //         , 100);
    // }, []);

    // const showToast = (msg, type, delay = 2000) => {
    //     toast[type](msg, {
    //       position: "bottom-right",
    //       autoClose: delay,
    //       hideProgressBar: false,
    //       closeOnClick: true,
    //       pauseOnHover: true,
    //       draggable: true,
    //       progress: undefined,
    //       theme: "dark",
    //       transition: Slide,
    //     });
    //   };

    // // code to monitor change in someVariable
    // useEffect(() => {
    //     showToast(`Value of someVariable changed to ${someVariable}`, 'success', 500);
    // }, [someVariable]);



    useEffect(() => {

        return;

       
        let frameCount = 0;
        let dataCount = 0;
        const websocket = new WebSocket('ws://10.0.63.153:3010/ws');
        websocket.binaryType = 'blob';  // Since the images can be in binary format

        const updateFpsAndSpeed = () => {
            setFps(frameCount);
            setSpeed(dataCount / 1024);  // Speed in KB/s
            frameCount = 0;
            dataCount = 0;
        };

        const intervalId = setInterval(updateFpsAndSpeed, 1000); // Update FPS and speed every second

        websocket.onmessage = (event) => {
            const url = URL.createObjectURL(event.data);
            setImageSrc(url);
            frameCount++;
            dataCount += event.data.size;  // Add the size of the received data
        };

        websocket.onopen = () => console.log('Connected to server');
        websocket.onerror = (error) => console.error('WebSocket error:', error);
        websocket.onclose = () => {
            console.log('Disconnected from server');
            clearInterval(intervalId);
        };

        return () => {
            websocket.close();
            clearInterval(intervalId);
        };
    }, []);

    return (
        <div>

            <h1>Hologram generator</h1>
            <h1>{someVariable}</h1>
            <ToastContainer />
            
        </div>
    );
}

export default ImageViewer;
