import React, { useState, useEffect, useRef } from 'react';
import { Collapse, CardBody, Card, CardTitle, Input, } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronUp } from '@fortawesome/free-solid-svg-icons';
import { Form, Button, Row, Container, Col, FormGroup, Modal } from 'react-bootstrap';
import { useGlobalContext } from '../../GlobalContext';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide } from 'react-toastify';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import p5 from 'p5';

function SLMOptions() {


    const { serverInfo, slmOptions, setSLMOptions, canvasRefs } = useGlobalContext();
    const [activeButtonSpotAction, setActiveButtonSpotAction] = useState("Add");



    const toggle = () => {
        setSLMOptions(prev => ({ ...prev, isCollapsed: !prev.isCollapsed }));
    };

    const handleUpdateAffine = () => {
    }


    const [points, setPoints] = useState([]);
    const [selectedPoint, setSelectedPoint] = useState(null);
    const [dragging, setDragging] = useState(false);

    useEffect(() => {
        const canvas = canvasRefs.current[4];
        const ctx = canvas.getContext('2d');
        canvas.width = 800;
        canvas.height = 600;

        const drawPoints = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            points.forEach(point => {
                const { x, y, intensity } = point;
                const colorValue = Math.floor(intensity * 255);
                ctx.fillStyle = `rgb(${colorValue}, 0, ${255 - colorValue})`;
                ctx.beginPath();
                ctx.arc(x, y, 5, 0, Math.PI * 2);
                ctx.fill();
                if (point === selectedPoint) {
                    ctx.strokeStyle = 'white';
                    ctx.lineWidth = 2;
                    ctx.stroke();
                }
            });
        };

        drawPoints();

        const handleDoubleClick = (e) => {
            const rect = canvas.getBoundingClientRect();
            const x = e.clientX - rect.left;
            const y = e.clientY - rect.top;
            const newPoint = { x, y, z: 0, intensity: 0 };
            setPoints([...points, newPoint]);
        };

        const handleContextMenu = (e) => {
            e.preventDefault();
            const rect = canvas.getBoundingClientRect();
            const x = e.clientX - rect.left;
            const y = e.clientY - rect.top;
            const clickedPoint = points.find(p => Math.hypot(p.x - x, p.y - y) < 5);
            if (clickedPoint) {
                const newX = prompt('Enter new x value', clickedPoint.x);
                const newY = prompt('Enter new y value', clickedPoint.y);
                const newZ = prompt('Enter new z value', clickedPoint.z);
                const newIntensity = prompt('Enter new intensity value (0-1)', clickedPoint.intensity);
                if (newX && newY && newZ && newIntensity) {
                    clickedPoint.x = parseFloat(newX);
                    clickedPoint.y = parseFloat(newY);
                    clickedPoint.z = parseFloat(newZ);
                    clickedPoint.intensity = parseFloat(newIntensity);
                    setPoints([...points]);
                }
            }
        };

        const handleMouseDown = (e) => {
            const rect = canvas.getBoundingClientRect();
            const x = e.clientX - rect.left;
            const y = e.clientY - rect.top;
            const clickedPoint = points.find(p => Math.hypot(p.x - x, p.y - y) < 5);
            if (clickedPoint) {
                setSelectedPoint(clickedPoint);
                setDragging(true);
            }
        };

        const handleMouseMove = (e) => {
            if (dragging && selectedPoint) {
                const rect = canvas.getBoundingClientRect();
                const x = e.clientX - rect.left;
                const y = e.clientY - rect.top;
                selectedPoint.x = x;
                selectedPoint.y = y;
                setPoints([...points]);
            }
        };

        const handleMouseUp = () => {
            setDragging(false);
            setSelectedPoint(null);
        };

        canvas.addEventListener('dblclick', handleDoubleClick);
        canvas.addEventListener('contextmenu', handleContextMenu);
        canvas.addEventListener('mousedown', handleMouseDown);
        canvas.addEventListener('mousemove', handleMouseMove);
        canvas.addEventListener('mouseup', handleMouseUp);

        return () => {
            canvas.removeEventListener('dblclick', handleDoubleClick);
            canvas.removeEventListener('contextmenu', handleContextMenu);
            canvas.removeEventListener('mousedown', handleMouseDown);
            canvas.removeEventListener('mousemove', handleMouseMove);
            canvas.removeEventListener('mouseup', handleMouseUp);
        };
    }, [points, selectedPoint, dragging]);





    return (
        <div>
            <div onClick={toggle} style={{ backgroundColor: "white", display: 'flex', justifyContent: 'space-between', borderTopLeftRadius: '10px', borderTopRightRadius: '10px', borderBottomLeftRadius: '0px', borderBottomRightRadius: '0px' }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <div className='fs-5 fw-bold' style={{ margin: '0px', padding: '10px', color: "black" }}>
                        SLM Options
                    </div>

                    <div style={{
                        height: '10px',
                        width: '10px',
                        backgroundColor: slmOptions.isOnline ? 'green' : 'red',
                        borderRadius: '50%',
                        display: 'inline-block',
                        marginLeft: '5px'
                    }}>
                    </div>
                </div>

                <div className='fs-5 fw-bold' style={{ marginLeft: 'auto', padding: '10px', color: "black" }}>
                    <FontAwesomeIcon icon={slmOptions.isCollapsed ? faChevronUp : faChevronDown} />
                </div>
            </div>

            <Collapse isOpen={slmOptions.isCollapsed}>
                <Card className="rounded-bottom">
                    <CardBody>
                        <h5>Focal Spots:</h5>
                        <div className='container'>
                            <div className="row">
                                <Form className="container">
                                    <div className="row form-group" style={{ marginBottom: '0' }}>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input
                                                type="number"
                                                id={`x_point`}
                                                className="form-control"
                                                placeholder={`x`}
                                                style={{ margin: '0', borderRadius: '0' }}
                                            />
                                        </div>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input
                                                type="number"
                                                id={`y_point`}
                                                className="form-control"
                                                placeholder={`y`}
                                                style={{ margin: '0', borderRadius: '0' }}
                                            />
                                        </div>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input
                                                type="number"
                                                id={`z_point`}
                                                className="form-control"
                                                placeholder={`z`}
                                                style={{ margin: '0', borderRadius: '0' }}
                                            />
                                        </div>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input
                                                type="number"
                                                id={`intensity_point`}
                                                className="form-control"
                                                placeholder={`i`}
                                                style={{ margin: '0', borderRadius: '0' }}
                                            />
                                        </div>
                                    </div>

                                </Form>
                                <div className="row mt-3" style={{ marginBottom: '0' }}>
                                    <ButtonGroup aria-label="Button group">
                                        {['Add', 'Remove', 'Drag'].map((action) => {


                                            return (
                                                <Button
                                                    key={action}
                                                    onClick={() => setActiveButtonSpotAction(action)}
                                                    variant={activeButtonSpotAction === action ? 'primary' : 'secondary'}

                                                >
                                                    {action}
                                                </Button>
                                            );
                                        })}
                                    </ButtonGroup>
                                </div>
                                <button type="submit" className="btn btn-primary" style={{ width: '100%', marginTop: '10px' }}>Process</button>

                            </div>


                        </div>
                        <h5>Affine Parameters:</h5>
                        <div className="container">

                            <div className="row">

                                <Form>
                                    {['0', '1', '2'].map((num) => (
                                        <div className="row form-group" key={num} style={{ marginBottom: '0' }}>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`SX${num}`}
                                                    className="form-control"
                                                    placeholder={`SX${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`SY${num}`}
                                                    className="form-control"
                                                    placeholder={`SY${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`CX${num}`}
                                                    className="form-control"
                                                    placeholder={`CX${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`CY${num}`}
                                                    className="form-control"
                                                    placeholder={`CY${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                        </div>
                                    ))}

                                </Form>
                                <button type="submit" className="btn btn-primary" style={{ width: '100%', marginTop: '10px' }}>Update Affine</button>





                            </div>
                        </div>
                    </CardBody>
                </Card>
            </Collapse>

        </div>
    );
}

export default SLMOptions;
