import React, { useState, useEffect, useRef } from 'react';
import { Collapse, CardBody, Card } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faChevronUp } from '@fortawesome/free-solid-svg-icons';
import { Form, Button, ButtonGroup } from 'react-bootstrap';
import { useGlobalContext } from '../../GlobalContext';


function SLMOptions() {
    const { serverInfo, slmOptions, setSLMOptions, canvasRefs } = useGlobalContext();
    const [activeButtonSpotAction, setActiveButtonSpotAction] = useState("Add");

    const toggle = () => {
        setSLMOptions(prev => ({ ...prev, isCollapsed: !prev.isCollapsed }));
    };

    const [points, setPoints] = useState({});
    const [selectedPoint, setSelectedPoint] = useState(null);

    const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });





    useEffect(() => {
        

        const canvas = canvasRefs[4].current;
        const context = canvas.getContext('2d');

        const addPoint = () => {
            
            const newPoint = [mousePosition.x, mousePosition.y, slmOptions.currentZ, slmOptions.currentIntensity];
            const key = `${newPoint[0]}-${newPoint[1]}`;
            console.log("Add Point", key);
            setPoints(prev => ({ ...prev, [key]: newPoint }));
        }

        const removePoint = () => {
            let nearestPointKey = null;
            let minDistance = 9999999999;
            console.log("Remove Point");

            for (const key in points) {
                const point = points[key];
                const distance = Math.pow(point[0] - mousePosition.x, 2) + Math.pow(point[1] - mousePosition.y, 2);
                if (distance < minDistance) {
                    minDistance = distance;
                    nearestPointKey = key;
                    console.log("Nearest Point", nearestPointKey);
                }
            }
            console.log(nearestPointKey);
            console.log(minDistance);


            console.log("Removing Point", nearestPointKey);

            setPoints(prev => {
                const newPoints = { ...prev };
                // check if key exists
                if (newPoints[nearestPointKey])
                    console.log("Key exists");
                else
                    console.log("Key does not exist");
                delete newPoints[nearestPointKey];
                return newPoints;
            });


        }
        const handleMouseMove = (event) => {
            const rect = canvas.getBoundingClientRect();
            const mouseX = event.clientX - rect.left;
            const mouseY = event.clientY - rect.top;

            setMousePosition({ x: mouseX, y: mouseY });

        
            console.log("Mouse Position", mouseX, mouseY);
            console.log(mousePosition);
        }

        const handleKeyPress = (event) => {
            switch (event.key.toUpperCase()) {
                case 'A':
                    // add point
                    addPoint();
                    break;
                case 'R':
                    // remove point
                    removePoint();
                    break;
                case 'M':
                    // If a point is already selected, release it
                    // if (selectedPoint) {
                    //     setSelectedPoint(null);
                    // } else {
                    //     // Otherwise, select the nearest point to the mouse
                    //     let nearestPointKey = null;
                    //     let minDistance = Infinity;

                    //     for (const key in points) {
                    //         const point = points[key];
                    //         const distance = Math.sqrt(Math.pow(point[0] - mouseX, 2) + Math.pow(point[1] - mouseY, 2));
                    //         if (distance < minDistance) {
                    //             minDistance = distance;
                    //             nearestPointKey = key;
                    //         }
                    //     }

                    //     if (minDistance <= 10) {
                    //         setSelectedPoint(nearestPointKey);
                    //     }
                    // }
                    break;
                case 'E':
                    //am
                    break;
                default:
                    break;
            }
        }

        window.addEventListener('mousemove', handleMouseMove);
        window.addEventListener('keypress', handleKeyPress);

        // Cleanup function to remove the event listener when the component unmounts
        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
            window.removeEventListener('keypress', handleKeyPress);
        }
    }, []);

    useEffect(() => {
        console.log("Points", points);
        const canvas = canvasRefs[4].current;
        const context = canvas.getContext('2d');

        context.clearRect(0, 0, canvas.width, canvas.height);
        for (const key in points) {
            const point = points[key];
            context.beginPath();
            context.arc(point[0], point[1], 5, 0, 2 * Math.PI);
            context.fillStyle = 'red';
            context.fill();


        }

    }, [points]);




    return (
        <div>

            <div onClick={toggle} style={{ backgroundColor: "white", display: 'flex', justifyContent: 'space-between', borderTopLeftRadius: '10px', borderTopRightRadius: '10px', borderBottomLeftRadius: '0px', borderBottomRightRadius: '0px' }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <div className='fs-5 fw-bold' style={{ margin: '0px', padding: '10px', color: "black" }}>
                        SLM Options
                    </div>

                    <div style={{
                        height: '10px',
                        width: '10px',
                        backgroundColor: slmOptions.isOnline ? 'green' : 'red',
                        borderRadius: '50%',
                        display: 'inline-block',
                        marginLeft: '5px'
                    }}>
                    </div>
                </div>

                <div className='fs-5 fw-bold' style={{ marginLeft: 'auto', padding: '10px', color: "black" }}>
                    <FontAwesomeIcon icon={slmOptions.isCollapsed ? faChevronUp : faChevronDown} />
                </div>
            </div>

            <Collapse isOpen={slmOptions.isCollapsed}>
                <Card className="rounded-bottom">
                    <CardBody>
                        <h5>Focal Spots:</h5>
                        <div className='container'>
                            <div className="row">
                                <Form className="container">
                                    <div className="row form-group" style={{ marginBottom: '0' }}>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input
                                                type="number"
                                                id={`x_point`}
                                                className="form-control"
                                                placeholder={`x`}
                                                style={{ margin: '0', borderRadius: '0' }}
                                            />
                                        </div>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input
                                                type="number"
                                                id={`y_point`}
                                                className="form-control"
                                                placeholder={`y`}
                                                style={{ margin: '0', borderRadius: '0' }}
                                            />
                                        </div>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input
                                                type="number"
                                                id={`z_point`}
                                                className="form-control"
                                                placeholder={`z`}
                                                style={{ margin: '0', borderRadius: '0' }}
                                            />
                                        </div>
                                        <div className="col-md-3" style={{ padding: '0' }}>
                                            <input
                                                type="number"
                                                id={`intensity_point`}
                                                className="form-control"
                                                placeholder={`i`}
                                                style={{ margin: '0', borderRadius: '0' }}
                                            />
                                        </div>
                                    </div>
                                </Form>
                                <div className="row mt-3" style={{ marginBottom: '0' }}>
                                    <ButtonGroup aria-label="Button group">
                                        {['Add', 'Remove', 'Drag'].map((action) => {
                                            return (
                                                <Button
                                                    key={action}
                                                    onClick={() => setActiveButtonSpotAction(action)}
                                                    variant={activeButtonSpotAction === action ? 'primary' : 'secondary'}
                                                >
                                                    {action}
                                                </Button>
                                            );
                                        })}
                                    </ButtonGroup>
                                </div>
                                <button type="submit" className="btn btn-primary" style={{ width: '100%', marginTop: '10px' }}>Process</button>
                            </div>
                        </div>
                        <h5>Affine Parameters:</h5>
                        <div className="container">
                            <div className="row">
                                <Form>
                                    {['0', '1', '2'].map((num) => (
                                        <div className="row form-group" key={num} style={{ marginBottom: '0' }}>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`SX${num}`}
                                                    className="form-control"
                                                    placeholder={`SX${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`SY${num}`}
                                                    className="form-control"
                                                    placeholder={`SY${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`CX${num}`}
                                                    className="form-control"
                                                    placeholder={`CX${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                            <div className="col-md-3" style={{ padding: '0' }}>
                                                <input
                                                    type="number"
                                                    id={`CY${num}`}
                                                    className="form-control"
                                                    placeholder={`CY${num}`}
                                                    style={{ margin: '0', borderRadius: '0' }}
                                                />
                                            </div>
                                        </div>
                                    ))}
                                </Form>
                                <button type="submit" className="btn btn-primary" style={{ width: '100%', marginTop: '10px' }}>Update Affine</button>
                            </div>
                        </div>
                    </CardBody>
                </Card>
            </Collapse>
        </div>
    );
}

